  end

  describe "GET /edit" do
    it "renders a successful response" do
      collection_tag = CollectionTag.create! valid_attributes
      get edit_collection_tag_url(collection_tag)
      expect(response).to be_successful
    end
  end

  describe "POST /create" do
    context "with valid parameters" do
      it "creates a new CollectionTag" do
        expect do
          post collection_tags_url, params: { collection_tag: valid_attributes }
        end.to change(CollectionTag, :count).by(1)
