      it "updates the requested collection_tag" do
        collection_tag = CollectionTag.create! valid_attributes
        patch collection_tag_url(collection_tag), params: { collection_tag: new_attributes }
        collection_tag.reload
        expect(collection_tag.name).to eq("New name")
